"use strict";document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector("#sticky-header-with-topbar");if(e){const t=e.offsetTop;window.addEventListener("scroll",()=>{window.pageYOffset>t?e.classList.add("sticky"):e.classList.remove("sticky")})}const o=document.querySelectorAll(".owl-carousel");o.length&&o.forEach(e=>{new OwlCarousel(e,{items:1,loop:!0,nav:!0,dots:!0,autoplay:!0,autoplayTimeout:5e3})})});
// Lazy load images
document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll("img[data-src]");const t=new IntersectionObserver((e,t)=>{e.forEach(e=>{if(e.isIntersecting){const n=e.target;n.src=n.dataset.src;n.removeAttribute("data-src");t.unobserve(n)}})});e.forEach(e=>t.observe(e))});